public class Assignment4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		multipliction(4);

	}

	public static void multipliction(int N) {

		for (int i = 1; i <= 10; i++) {

			int mul = i * N;

			System.out.println("Multipliction of " + N + " * " + i + ":  " +mul);

		}

	}

}
